sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    getExistingAttachments();

    function getExistingAttachments() {

        var existingAttach = new GlideRecord('sys_attachment');
        existingAttach.orderBy('sys_created_on');
        existingAttach.query();
        while (existingAttach.next()) {

                var ga = new GlideSysAttachment();
                var attachmentStream2 = ga.getContentStream(existingAttach.sys_id);
                var secondHashValue = new GlideDigest().getSHA256HexFromInputStream(attachmentStream2);
                var currentHash = getCurrentAttachment();

                if (secondHashValue == currentHash) {
                    gs.addInfoMessage("attachment already exists!");
                    existingAttach.deleteMultiple();
                    break;
                } else
                    continue;

        }
    }

    function getCurrentAttachment() {

        var sysAttachment = new GlideSysAttachment();
        var attachmentGR = sysAttachment.getAttachments('x_pir_car', current.table_sys_id);
        attachmentGR.next();
        var attachmentStream1 = sysAttachment.getContentStream(attachmentGR.sys_id);
        var firstHashValue = new GlideDigest().getSHA256HexFromInputStream(attachmentStream1);

        return firstHashValue;

    }

})(current, previous);
