sys_script_include.script:
var ParkingIssue = Class.create();
ParkingIssue.prototype = {
    initialize: function() {},

    create: function(car, description) {

        if (description == 'undefined')
            description = "No description provided";

        var grPI = new GlideRecordSecure('x_pir_parking_issue');
        grPI.initialize();
        grPI.setValue('description', description);
        grPI.setValue('car', car.sys_id);
        grPI.insert();
        return grPI;
    },

    type: 'ParkingIssue'
};
